const url = window.location.href;

console.log(url);

let newUrl;
if(url.indexOf('animerulz.pro') != -1){
    newUrl = url.replace('animerulz.pro', 'animerulz.to');
    let localData;
    if(!localStorage.getItem('isMigrated')){
        localData = JSON.stringify(localStorage);
        localStorage.setItem('isMigrated', true)
        window.open(newUrl + `?ls=${localData}`, '_self');
    }else{
        window.open(newUrl, '_self');
    }

}else{
    newUrl = url.replace('animerulz.in', 'animerulz.to');
    window.open(newUrl, '_self');
}

